// page init
jQuery(function(){
	initCustomForms();
	initSameHeight();
	initMultiColumns();
	initMobileNav();
	jQuery('input, textarea').placeholder();
});

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		menuActiveClass: 'nav-active',
		menuOpener: '.opener-menu'
	});
}

// initialize custom form elements
function initCustomForms() {
	jcf.setOptions('Select', {
		wrapNative: false
	});
	jcf.replaceAll();
}

// align blocks height
function initSameHeight() {
	jQuery('.icon-list').sameHeight({
		elements: 'li',
		leftEdgeClass: 'first',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});
}

// split content into columns
function initMultiColumns() {
	var list = jQuery('.list');
	var items = list.find('>li');

	items.each(function() {
		jQuery(this).addClass('dontsplit');
	});

	list.columnize({
		columns: 2
	});
}

